mapper.update:
begin

	update TBL_ORDER set orderState = #{orderState}
						, cancel = 0
						, exchange = 0
						, refund = 0
	where orderCode = #{orderCode};


	update TBL_ORDERLIST set orderState = #{orderState} where orderCode = #{orderCode};



		update TBL_ORDER set cancel = 1 where orderCode = #{orderCode};

end;
