mapper.insert:
insert into TBL_QNABOARD (USERID,
					 USEREMAIL,
					 USERPHONE,
					 QNABOARDCODE,
					 QNABOARDSUBJECT,
					 QNABOARDCONTENT,
					 THUMBIMG,
					 SECRET,
					 PRODUCTCODE,
					 PRODUCTNAME,
					 DISCOUNTPRICE,
					 REF) 
			values (#{userId} 
					,#{userEmail} 
					,#{userPhone}
					,qnaboardcode_seq.nextval
					,#{qnaboardSubject}
					,#{qnaboardContent}
					,#{thumbImg}
					,#{secret}
					,#{productCode}
					,#{productName}
					,#{discountPrice}
					,qnaboardcode_seq.currval)

mapper.update:
update TBL_QNABOARD set
USEREMAIL=#{userEmail}, USERPHONE=#{userPhone}, QNABOARDSUBJECT=#{qnaboardSubject}, QNABOARDCONTENT=#{qnaboardContent},
SECRET=#{secret}
where QNABOARDCODE =#{qnaboardCode}
