					userEmail,
					productCode,
					productName,
					thumbImg,
					discountPrice,
					productOption,
					productQty,
					optionContent)
					values(#{userId}
							,#{userEmail}
							,#{productCode}
							,#{productName}
					   		,#{thumbImg}
					   		,#{discountPrice}
					   		,#{productOption}
					   		,#{productQty}
					   		,#{optionContent})
