mapper.update:
update TBL_ORDER set orderState = #{orderState}
			where orderCode = #{orderCode} 
			and productCode = #{productCode}

begin
update TBL_ORDER set orderState = #{orderState[0]} , cancel = 0 , exchange = 0 , refund = 0 where orderCode in ${check[i]};
update TBL_ORDERLIST set orderState = ${orderState[0]} where orderCode in ${check[i]};
update TBL_ORDER set cancel=1, orderState=0, cancelAmount=totalPrice, cancelQty=purchseQty where orderCode in ${check[i]};
update TBL_ORDER set exchange=1, orderState=6, exchangeQty=purchaseQty where orderCode in ${check[i]};
update TBL_ORDER set refund=1, orderState=8, refundQty=purchaseQty, refundAmount=totalPrice where orderCode in ${check[i]};
end;

mapper.delete:
begin
	delete TBL_ORDERLIST where orderCode in 
	${check[i]};

	delete TBL_ORDER where orderCode in
	${check[i]};
end;

mapper.update:
update TBL_ORDER set cancel = 1,
					orderState = 0,
					cancelQty = #{purchaseQty},
