					   , discountPrice
					   , logdate)
				  values(#{userId}
				  	   , #{productCode}
				  	   , #{productName}
				  	   , #{productOption}
				  	   , #{thumbImg}
				  	   , #{discountPrice}
				  	   , sysdate)
